jQuery(document).ready(function(){jQuery('.ctsc-map').each(function(){var data=jQuery(this).data(),options={center:new google.maps.LatLng(data.lat,data.lng),disableDefaultUI:data.controls||false,zoom:data.zoom||15,mapTypeId:google.maps.MapTypeId.ROADMAP};var styles=[{stylers:[{hue:data.color},{saturation:-20}]},{featureType:"road",elementType:"labels",stylers:[{visibility:"off"}]}];var map=new google.maps.Map(this,options);map.setOptions({styles:styles});});ctsc_waypoint_fade();ctsc_waypoint_progress();jQuery('.ctsc-back-top').click(function(e){e.preventDefault();var target_id=jQuery(this).attr('href');jQuery('html, body').animate({scrollTop:jQuery(target_id).offset().top},1000);});});function ctsc_waypoint_fade(){if(jQuery.isFunction(jQuery.fn.waypoint)){jQuery('.ctsc-animation').waypoint(function(){var area_delay=0;var element=jQuery(this);if(jQuery(this).attr('data-delay'))area_delay=jQuery(this).attr('data-delay');setTimeout(function(){element.addClass('ctsc-animation-active');},area_delay);},{offset:'80%'});}}
function ctsc_waypoint_progress(){if(jQuery.isFunction(jQuery.fn.waypoint)){jQuery('.ctsc-progress .bar-content').waypoint(function(){var element=jQuery(this);var progress_data=element.data();element.animate({width:progress_data.value+'%'},1500);},{offset:'95%'});}};